graphql.query:
query getMeAllContacts {
    allContacts {
        id
        firstName
        lastName
        phone
        email
    }
}

query getMeSpecificContact {
    getContactById(contactId: 2) {
        firstName
        title
        email
    }
}

query getMeSpecificContact {
    getContactByEmail(email: "d.shrute@dundermifflin.com") {
        firstName
        title
        email
    }
}

query getMeSpecificContact {
    getContactByName(name: "jim") {
        firstName
        title
        email
    }
}

query getMeSpecificContact {
    searchContactByEmail(email: "shru") {
        firstName
        title
        phone
    }
}

query getMeSpecificContact {
    searchContactByName(name: "shru") {
        firstName
        title
        phone
    }
}
